<script>
	import {
		uniIcon
	} from '@dcloudio/uni-ui';
	import moment from "moment";
	import MxDatePicker from "../../components/mx-datepicker/mx-datepicker.vue";
	export default {
		components: {
			uniIcon,
			MxDatePicker
		},
		data() {
			return {
				// 总成交额
				totalTurnover: 0,
				// 团队人数
				teamSize: 0,
				// 今日成交
				todayTurnover: 0,
				// 今日邀请人数
				todayInvit: 0,
				// 是否展示时间选择
				showPicker: false,
				// 开始时间
				startDate: '',
				// 结束时间
				endDate: '',
				// 标志开始时间还是结束时间
				type: 'start',
				// picker的返回值
				timeValue: "",
				// 时间段内总成交
				totalTurnoverInPeriod: 0,
				// 时间段内要求人数
				invitNumInPeriod: 0,
				// 时间段内新增优惠积分
				addedScore: 0
				
			}
		},
		computed: {
			isNumInvaild() {
				return this.mbValue - 0 < this.num - 0
			}
		},
		methods: {
			init() {
				this.initInfo();
				this.initTime();
			},
			initInfo(){
				this.teamSize = 9029;
				this.totalTurnover = 10299.99;
				this.todayTurnover = 3456.88;
				this.todayInvit = 399;
				
				// 默认搜索
				this.getdata();
			},
			// 初始化时间
			initTime(){
				//获取当前时间
				let now = moment();
				// 结束时间为当前
				this.endDate = now.format("YYYY年MM月DD日");
				// 开始时间为7天前
				this.startDate = now.subtract(7, 'days').format("YYYY年MM月DD日");
			},
			onShowDatePicker(type) { //显示
				this.showPicker = true;
				this.type = type;
			},
			onSelected(e) { //选择
				this.showPicker = false;
				if (e) {
					this[`${this.type}Date`] = moment(e.value).format("YYYY年MM月DD日");
				}
			},
			// 
			changeTab(tab){
				this.currentTab = tab;
			},
			// 查询
			search(){
				// 结束时间不早于开始时间
				let start = this.startDate.replace(/年/g, "-").replace(/月/g, "-").replace(/日/g, "");
				let end = this.endDate.replace(/年/g, "-").replace(/月/g, "-").replace(/日/g, "");
				if(moment(end).isBefore(start)) {
					uni.showToast({
						icon: "none",
						title: "结束时间不能早于开始时间"
					})
				}
				
				// 
				this.getdata();
			},
			getdata(){
				this.totalTurnoverInPeriod = 3230;
				this.invitNumInPeriod = 240;
				this.addedScore = 435340;
			}
		},
		onLoad(options) {
			// 我的团队中查看 经理业绩
			if(options.role) {
				uni.setNavigationBarTitle({
					title: "经理业绩查询"
				})
			}
			this.init();
		}
	}
</script>

